<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use File;use Illuminate\Database\Eloquent\Builder;class Ad extends Model{    /**     * The database table used by the model.     *     * @var string     */    protected $table = 'ads';    /**    * The database primary key value.    *    * @var string    */    protected $primaryKey = 'id';                   /**     * belongs To relation User     */    /**     * The attributes that should be hidden for arrays.     *     * @var array     */    protected $hidden = [        'created_at', 'updated_at'    ];    /**     * belongs To relation Product     */    public function store()    {        return $this->belongsTo(Store::class,'store_id');    }    /**     * Attributes that should be mass-assignable.     *     * @var array     */    protected $fillable = ['id','store_id', 'company_id', 'time','media','media_type','created_at', 'updated_at'];    protected $appends = array('mediaPath');//    public function getthumbnailAttribute()//    {//        $image_name = 'no_image.png';//        if($this->attributes['image']){//            $image_name = 'ads/thumbs/'.$this->attributes['image'];//        }//        //return 'hello';//        return checkImage($image_name);////    }    public function getMediaPathAttribute()    {        $image_name = 'no_image.png';        if($this->attributes['media']){            $image_name = 'ads/'.$this->attributes['media'];        }        return checkImage($image_name);    }}