<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use File;use Illuminate\Database\Eloquent\Builder;class Order extends Model{    /**     * The database table used by the model.     *     * @var string     */    protected $table = 'orders';    /**    * The database primary key value.    *    * @var string    */    protected $primaryKey = 'id';                   /**     * belongs To relation Store     */    public function store()    {    	return $this->belongsTo(Store::class);    }        /**     * belongs To relation Customer     */    public function customers()    {    	return $this->belongsTo(Customer::class,'customer');    }        /**     * belongs To relation Shipping_option     */    public function shipping_option()    {    	return $this->belongsTo(Shipping_option::class,'shipping_id');    }        /**     * Scopes    */        /**     * Scope a query to only include popular users.     *     * @param \Illuminate\Database\Eloquent\Builder $query and $company_id     * @return \Illuminate\Database\Eloquent\Builder     */    public function scopeCustomer (Builder $query, $customer) {                return $query->whereHas('customers', function ($q) use ($customer) {            $q->where('first_name', 'ilike', "$customer%")                ->orWhere('last_name', 'ilike', "$customer%")                ->orWhere('email', 'ilike', "$customer%")                ->orWhere('mobile', 'like' , "$customer%");        });            }          /**     * boot     */    protected static function boot ()    {    	parent::boot();    	static::deleting(function($product) {                                                	});    }    /**     * The attributes that should be hidden for arrays.     *     * @var array     *///   protected $hidden = [//       'is_active', 'created_at', 'updated_at', 'deleted_at', 'is_modifier'//   ];        /**     * Attributes that should be mass-assignable.     *     * @var array     */    protected $fillable = ['reference', 'biller_id', 'biller_detail', 'store_id', 'customer','is_mobile_order', 'discount', 'shipping_cost', 'order_status', 'payment_status', 'payment_method', 'payment_detail', 'payment_received', 'order_note', 'staff_note', 'order_total', 'service_fee', 'sub_total', 'order_items','order_tax','shipping_id','shipping_detail','basket_size','cost_of_goods','order_id','return_ids','table_data','dine_option','tip','customer_order_status','global_discount'];}