<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use File;use Illuminate\Database\Eloquent\Builder;class Shifts extends Model{    /**     * The database table used by the model.     *     * @var string     */    protected $table = 'shifts';    /**    * The database primary key value.    *    * @var string    */    protected $primaryKey = 'id';                   /**     * belongs To relation Store     */    public function store()    {    	return $this->belongsTo(Store::class);    }        /**     * belongs To relation Customer     */    /**     * belongs To relation user     */    public function user()    {        return $this->belongsTo(User::class, 'user_id');    }         protected $fillable = ['user_id', 'start_time', 'end_time', 'minutes', 'starting_balance','total_balance', 'transaction', 'logs', 'store_id'];}